import { useState } from "react";
import ToDoForm from "./components/ToDoForm";
import ToDoList from "./components/ToDoList";

export default function App() {
  const [listItems, setListItems] = useState(Array());

  function removeListItem(itemId) {
    const newItems = listItems.filter(item.id != itemId);
    setListItems(newItems);
  }

  function addListItem(text) {
    const newList = [...listItems, { text: text, id: listItems.length }];
    setListItems(newList);
  }
  return (
    <>
      <ToDoList removeItem={removeListItem} />
      <ToDoForm onSubmit={addListItem} />
      <div></div>
    </>
  );
}
