import React from "react";

function togglePopup() {
  let popup = document.getElementById("input-popup");
  popup.classList.toggle("show");
  return;
}

export default function ToDoForm({ onSubmit }) {
  function addTask(e) {
    e.preventDefault();
    const form = e.target;
    const formData = new FormData(form);
    const input = formData.get("input");
    if (input.length) {
      onSubmit(input);
    } else {
      togglePopup();
    }
  }
  return (
    <form onSubmit={addTask}>
      <div className="form">
        <div className="popup" id="input-popup" onClick={togglePopup}>
          <p className="popup-text">This is a test</p>
        </div>
        <input name="input" className="form-input" />
        <button type="form-submit-button">Add task</button>
      </div>
    </form>
  );
}
